/*
 * Example Of Continue Statement 
 */

package com.loops;

	public class ContinueDemo {

		public static void main(String[] args) {
			// TODO Auto-generated method stub
			int i;
			for(i=1;i<=10;i++)
			{
				if(i==5)
					continue;
				System.out.println(i);
			}
		
		}

	}
